mapper.update:
update board 
    set title = #{title, jdbcType=VARCHAR}, 
        contents = #{contents, jdbcType=VARCHAR}, 
        writer = #{writer, jdbcType=VARCHAR} 
  where board_no = #{boardNo, jdbcType=INTEGER} and category_no = #{categoryNo, jdbcType=INTEGER}

mapper.delete:
	delete
	  from board
  	 where board_no = #{boardNo, jdbcType=INTEGER} and category_no = #{categoryNo, jdbcType=INTEGER}

mapper.select:
select * 
	from board
   where board_no= #{boardNo, jdbcType=INTEGER} and category_no = #{categoryNo, jdbcType=INTEGER}
